export function filterByDealContext(items: MasterItem[], intake: DealIntake): MasterItem[] {
  return items.filter((item) => {
    if (item.crossBorderFlag && !intake.crossBorder) return false;
    if (item.tsaRelevant && intake.tsaRequired === "no") return false;
    return true;
  });
}
